package com.fast.dev.pay.server.core.cpcn.conf;

import lombok.AllArgsConstructor;
import lombok.Data;
import lombok.NoArgsConstructor;
import org.springframework.boot.context.properties.ConfigurationProperties;
import org.springframework.stereotype.Component;

@Data
@Component
@NoArgsConstructor
@AllArgsConstructor
@ConfigurationProperties(prefix = "cpcn")
public class CPCNConf {

    //主机地址
    private String host = "https://test.cpcn.com.cn/Gateway/InterfaceII";

    //分账模式：
    //10=不分帐
    //20=按比例分账
    //30=按金额分账
    private String splitType = "10";

    //结算标识（不传 SplitType 时只支持不分账、
    //按金额分账的格式，结算标识不能重复。分
    //账实时扣收手续费时，使用排序第一的结算
    //标识扣收手续费)
    //SplitType=10（不分帐），单个结算标识，格式:0001
    //SplitType=20（按比例分账），多个结算标识，格式:0001_500000,0002_100000,0003_400000
    private String settlementFlag = "0001";


    //中金公钥
    public String pubKey="";


    public String priKey="";
}
